mapper.update:
update board set
B_TITLE=#{B_TITLE, jdbcType=VARCHAR}
, B_CONTENT=#{B_CONTENT, jdbcType=VARCHAR}
, B_IMG=#{B_IMG,
jdbcType=VARCHAR}
, G_TYPE=#{G_TYPE, jdbcType=VARCHAR}
where B_NUM=#{B_NUM}

update board set
B_GOOD=B_GOOD+1
where B_NUM = #{B_NUM}

mapper.select:
select * from (select rownum rnum, game.* from (select * from All_Game
G_TYPE like '%'||#{keyword}||'%' or G_NAME like '%'||#{keyword}||'%'
order by g_name asc) game) where rnum between #{startRow} and #{endRow}
